import React, { useState, useRef } from 'react'

import { Title, Favicon, DrawerHeader } from '../'
import { ElectroNextHomeContainer } from './styled'
import { Drawer, DrawerFooter, ButtonList } from "../"

export const ElectroNextHome = () => {

  const btnRef = useRef(null)

  const [show, setShow] = useState(false)
  const [show2, setShow2] = useState(false)
return (<>
    <Title>ElectroNext.js App Template </Title>
    <Favicon src='/img/favicon/electron.png'/>

    <ElectroNextHomeContainer >
      <div className='heart' onClick={() => {setShow2(!show2)}}>
        ❤️
      </div>
      <div className='icons'>
        <ElectronIcon/>
        <NextIcon/>
      </div>
      <div className='footer' >
        
      </div>
        <Drawer 
          btnRef={btnRef}
          visible={show}
          clickOut
          onClose={({clickOut}) => {
            setShow(!clickOut)
          }}
        > 
          <DrawerHeader>
            <ButtonList label='teste'/>
          </DrawerHeader>
          <DrawerFooter>
            <ButtonList label='teste'/>
          </DrawerFooter>
        </Drawer>
        <Drawer 
          placement='right'
          visible={show2}
        >
          teste2
        </Drawer>
    </ElectroNextHomeContainer>
  </>)
}

